mapper.select:
SELECT t.id, t.shop_id, t.origin, t.consumer, t.reg_date, t.contents, t.size, t.name,
t.cost, t.store , t.retail , t.wholesale , t.partner , t.agent,
s.name AS shop_name, CASE WHEN #{role}='STORE' THEN store WHEN #{role}='RETAILER' THEN retail WHEN #{role}='WHOLESALER' THEN wholesale WHEN #{role}='PARTNER' THEN partner WHEN #{role}='AGENT' THEN agent ELSE consumer END AS price FROM PRODUCT t, SHOP s WHERE t.shop_id = s.id
AND s.id = #{shopId}
AND size = #{size}
ORDER BY size asc LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT t.id, t.shop_id, t.origin, t.consumer, t.reg_date, t.contents, t.size, t.name,
t.cost, t.store , t.retail , t.wholesale , t.partner , t.agent,
s.name AS shop_name, CASE WHEN #{role}='STORE' THEN store WHEN #{role}='RETAILER' THEN retail WHEN #{role}='WHOLESALER' THEN wholesale WHEN #{role}='PARTNER' THEN partner WHEN #{role}='AGENT' THEN agent ELSE consumer END AS price FROM PRODUCT t, SHOP s WHERE t.shop_id = s.id AND t.id = #{id}
