mapper.select:
SELECT *, (SELECT area_name FROM AREA WHERE id = area_id) area_name
FROM PRICE_INFO
WHERE 1=1

    AND area_id = #{areaId}

ORDER BY id DESC
LIMIT #{cri.pageStart}, #{cri.perPageNum}

mapper.insert:
/* TODO AN 난가정보 등록 updateDate는 DB에서 알아서 시분초까지 들어가길!  */
    INSERT INTO PRICE_INFO (area_id, size1, size2, size3, size4, size5, size6, reg_date, memo)
    VALUES (#{areaId},#{size1},#{size2},#{size3},#{size4},#{size5},#{size6},NOW(),#{memo})

mapper.update:
UPDATE PRICE_INFO
SET size1 = #{size1}
,size2 = #{size2}
,size3 = #{size3}
,size4 = #{size4}
,size5 = #{size5}
,size6 = #{size6}
,memo = #{memo}
WHERE id = #{id}

UPDATE BOARD_MESSAGE
SET status = #{status}
, title = #{title}
, contents = #{contents}
, update_date = NOW()

mapper.select:
SELECT count(*) cnt
FROM BOARD_MESSAGE
WHERE 1=1

    AND board_id = #{boardId}


    AND status = #{status}


    AND ( (title LIKE CONCAT('%',#{searchText},'%')) OR (contents LIKE CONCAT('%',#{searchText},'%')) )

SELECT a.*, b.board_name
FROM BOARD_MESSAGE a, BOARD b
WHERE a.board_id = b.id

    AND a.board_id = #{boardId}


    AND a.status = #{status}


    AND ( (a.title LIKE CONCAT('%',#{searchText},'%')) OR (a.contents LIKE CONCAT('%',#{searchText},'%')) )

ORDER BY iD DESC
LIMIT #{cri.pageStart}, #{cri.perPageNum}

mapper.update:
UPDATE PRICE_CAST
SET cast = #{cast}
WHERE id = #{id}

UPDATE CONTACT_US
SET answer_flag = 1
, answer = #{answer}
, answer_date = NOW()
WHERE id = #{id}

mapper.select:
SELECT count(*) cnt FROM CONTACT_US
WHERE 1=1

    AND user_id = #{userId}


    AND answerFlag = #{answerFlag}

SELECT * FROM CONTACT_US
WHERE 1=1

    AND user_id = #{userId}


    AND answerFlag = #{answerFlag}

LIMIT #{cri.pageStart}, #{cri.perPageNum}
