mapper.select:
SELECT * FROM USER WHERE 1=1
AND status = #{status}
AND user_name LIKE CONCAT('%',#{userName},'%')
AND shop_name LIKE CONCAT('%',#{shopName},'%')
ORDER BY id desc LIMIT #{offset}, #{limit}

mapper.update:
UPDATE USER
SET pwd = #{pwd}
WHERE id = #{id}

UPDATE USER
SET role = #{role}
, status = #{status}
, user_name = #{userName}
, pwd = #{pwd}
, mobile = #{mobile}
, shop_name = #{shopName}
, post_num = #{postNum}
, address = #{address}
, address_detail = #{addressDetail}
, shop_tel = #{shopTel}
, profile_image = #{profileImage}
, longitude = #{longitude}
, latitude = #{latitude}
, update_date = NOW()
WHERE id = #{id}

UPDATE USER
SET status = 9
WHERE id = #{id}
