mapper.update:
UPDATE USER
SET pwd = #{pwd}
WHERE id = #{id}

UPDATE USER
SET role = #{role}
, status = #{status}
, user_name = #{userName}
, pwd = #{pwd}
, mobile = #{mobile}
, shop_name = #{shopName}
, post_num = #{postNum}
, address = #{address}
, address_detail = #{addressDetail}
, shop_tel = #{shopTel}
, profile_image = #{profileImage}
, longitude = #{longitude}
, latitude = #{latitude}
, update_date = NOW()
WHERE id = #{id}

UPDATE USER
SET login_count = login_count+1
WHERE id = #{id}

UPDATE USER
SET status = 9
WHERE id = #{id}

mapper.select:
/*TODO AN 전체 히스토리 가져오기*/
SELECT * FROM USER_ROLE_REQ
WHERE 1=1

    AND user_id = #{userId}


    AND status = #{status}


    AND req_name like concat('%',#{userId},'%')


    AND reg_date >= #{startDate}


    AND reg_date <= #{endDate}

order by id desc
LIMIT #{cri.pageStart}, #{cri.perPageNum}

/*TODO AN 전체 히스토리 가져오기*/
SELECT COUNT(*) FROM USER_ROLE_REQ
WHERE 1=1

    AND user_id = #{userId}


    AND status = #{status}


    AND req_name like concat('%',#{userId},'%')


    AND reg_date >= #{startDate}


    AND reg_date <= #{endDate}

